    private void bt_AddierenActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bt_AddierenActionPerformed
        float zahl_1 = Float.parseFloat(tf_ErsteZahl.getText());
        float zahl_2 = Float.parseFloat(tf_ZweiteZahl.getText());
        tf_Ergebnis.setText("" + (zahl_1 + zahl_2));
    }//GEN-LAST:event_bt_AddierenActionPerformed

